    {
      link = '#' + $mgt('#redactor_link_anchor').val();
      text = $mgt('#redactor_link_anchor_text').val();
    }      

    this._insertLink('<a href="' + link + '" target="' + target + '">' +  text + '</a>&nbsp;', $mgt.trim(text), link, target);

